select PROIZV.* from PROIZV JOIN PRODUCT ON PROIZV.ID_ORG = PRODUCT.ID_ORG WHERE PRODUCT.AVAILABLE_NUM > 50;
SELECT PROIZV.ORG_NAME AS Производитель, SUM(PRODUCT.COST) AS Общая_Стоимость from PROIZV JOIN PRODUCT ON PROIZV.ID_ORG = PRODUCT.ID_ORG WHERE PRODUCT.LOAD_DATE > '2024-01-01' GROUP BY PROIZV.ORG_NAME
SELECT DEAL.ID_DEAL, DEAL.DEAL_DATE, DEAL.END_DATE, DEAL.PTYPE, PROIZV.STATUS FROM DEAL JOIN PROIZV ON DEAL.ID_ORG = PROIZV.ID_ORG WHERE PROIZV.STATUS = 2